mapper.delete:
DELETE FROM payments
WHERE pay_no = #{payNo}
AND user_id = #{userId}

mapper.update:
UPDATE payments
SET pay_type='success',
    pay_date = sysdate
WHERE pay_no = #{payNo}
AND user_id = #{userId}

mapper.select:
    	SELECT down_cnt
		FROM payments
		WHERE pay_no= #{payNo}
		AND user_id=#{userId}

mapper.update:
    	UPDATE payments
		SET down_cnt = down_cnt+1
		WHERE pay_no= #{payNo}
		AND user_id=#{userId}
